mapper.select:
SELECT
FROM mvs m LEFT JOIN artists a ON m.artist_id = a.id AND a.deleted = 0 WHERE m.deleted = 0
AND m.name LIKE CONCAT('%', #{name}, '%')
AND m.artist_id = #{artistId}
ORDER BY m.created_at DESC LIMIT #{offset}, #{size}

SELECT COUNT(*) FROM mvs m WHERE m.deleted = 0
AND m.name LIKE CONCAT('%', #{name}, '%')
AND m.artist_id = #{artistId}

mapper.update:
UPDATE mvs SET deleted = 1, updated_at = NOW() WHERE id IN
#{id}
AND deleted = 0
