mapper.select:
SELECT

FROM songs
WHERE deleted = 0

    AND name LIKE CONCAT('%', #{name}, '%')


    AND artist_id = #{artistId}


    AND album_id = #{albumId}

ORDER BY created_at DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM songs
WHERE deleted = 0

    AND name LIKE CONCAT('%', #{name}, '%')


    AND artist_id = #{artistId}


    AND album_id = #{albumId}

mapper.update:
UPDATE songs SET deleted = 1, updated_at = NOW() WHERE id IN
#{id}
AND deleted = 0
