DROP TABLE IF EXISTS `mvs`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `mvs` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT 'MV视频ID，主键自增',
  `name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'MV视频名称',
  `artist_id` bigint NOT NULL COMMENT '演唱歌手ID，外键关联artists.id',
  `cover` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'MV封面图片URL',
  `video_path` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'MV视频文件存储路径',
  `duration` int NOT NULL COMMENT 'MV视频时长，单位秒',
  `description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci COMMENT 'MV详细描述和介绍',
  `play_count` bigint NOT NULL DEFAULT '0' COMMENT 'MV播放次数统计',
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'MV信息创建时间',
  `updated_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'MV信息最后更新时间',
  `deleted` tinyint NOT NULL DEFAULT '0' COMMENT '逻辑删除标记：0=正常，1=已删除',
  PRIMARY KEY (`id`),
  KEY `idx_name` (`name`),
  KEY `idx_artist_id` (`artist_id`),
  KEY `idx_play_count` (`play_count`),
  KEY `idx_deleted` (`deleted`),
  CONSTRAINT `fk_mvs_artist` FOREIGN KEY (`artist_id`) REFERENCES `artists` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=296 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='音乐视频表 - 存储音乐视频信息、播放量和关联歌手';
