mapper.select:
SELECT

FROM albums
WHERE deleted = 0

    AND name LIKE CONCAT('%', #{name}, '%')


    AND artist_id = #{artistId}


    AND YEAR(release_date) = #{releaseYear}

ORDER BY release_date DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM albums
WHERE deleted = 0

    AND name LIKE CONCAT('%', #{name}, '%')


    AND artist_id = #{artistId}


    AND YEAR(release_date) = #{releaseYear}

mapper.update:
UPDATE albums SET deleted = 1, updated_at = NOW() WHERE id IN
#{id}
AND deleted = 0
