mapper.select:
SELECT

FROM playlists
WHERE deleted = 0

    AND name LIKE CONCAT('%', #{name}, '%')


    AND user_id = #{userId}


    AND is_public = #{isPublic}

ORDER BY created_at DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM playlists
WHERE deleted = 0

    AND name LIKE CONCAT('%', #{name}, '%')


    AND user_id = #{userId}


    AND is_public = #{isPublic}
